import { createSlice } from '@reduxjs/toolkit';

const booksSlicer = createSlice({
    name: 'booksState',
    initialState: { books: [] },
    reducers: {
        setBooks: (state, action) => {
            state.books = action.payload;
        },
    },
});

export const {
    actions: { setBooks },
    reducer: booksReducer,
} = booksSlicer;
