import React, { useState, useMemo } from 'react';
import { Button } from '../../components/Button';

export const Login = () => {
    const [emailValue, setEmailValue] = useState('');
    const [passwordValue, setPasswordValue] = useState('');

    const emailHandler = (event: any) => {
        setEmailValue(event.target.value);
    };

    const passwordHandler = (event: any) => {
        setPasswordValue(event.target.value);
        console.log(typeof event);
    };

    const saveDataInput = () => {
        const inputValue = {
            email: emailValue,
            password: passwordValue,
        };
        console.log(inputValue);
    };

    return (
        <div className="flex items-center justify-center">
            <div className="flex flex-col justify-center items-start rounded-2xl border-2 border-neutral-300 w-2/5 max-w-3xl min-w-min my-40 p-10 shadow-xl">
                {/*w-2/5  */}
                {/*max-2xl:flex-col */}
                <div className="flex justify-center w-full">
                    <p className=" text-3xl font-bold p-2">Sign In</p>
                </div>

                <label className="font-bold my-2">Email adress</label>
                <input
                    placeholder="Enter email"
                    className="rounded-xl border-2 border-neutral-300 p-2 mb-2 w-full"
                    onChange={emailHandler}
                ></input>
                <label className="font-bold mb-2">Password</label>
                <input
                    placeholder="Password"
                    className="rounded-xl  border-2 border-neutral-300 p-2 w-full mb-4"
                    onChange={passwordHandler}
                ></input>

                <Button
                    content={'Submit'}
                    background="bg-indigo-600"
                    onClick={saveDataInput}
                    width="w-full"
                />

                <p>
                    Forgot{' '}
                    <a
                        className="text-blue-500 underline "
                        href="#"
                        target="_blank"
                    >
                        password?
                    </a>
                </p>
            </div>
        </div>
    );
};
