import React, { useState } from 'react';
import { Button } from '../Button';

interface props {
    totalBooks: number;
    booksPerPage: number;
    currentPage: number;
    setCurrentPage: (state: number) => void;
}

export const PaginationBooks = ({
    totalBooks,
    booksPerPage,
    currentPage,
    setCurrentPage,
}: props) => {
    //

    const [pageNumberLimit, setPageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setMaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setMinPageNumberLimit] = useState(0);
    //
    let pages = [];
    let envNumber = process.env.REACT_APP_PAGE_COUNT
        ? parseInt(process.env.REACT_APP_PAGE_COUNT)
        : 0;

    let envNumberChanged = [
        ...Array.from({ length: envNumber + 1 }, (_, i) => i),
    ];
    console.log(envNumberChanged);

    for (
        let i = 1;
        i <= Math.ceil(totalBooks / booksPerPage);
        // && i < envNumberChanged.length;
        i++
    ) {
        pages.push(i);
    }
    console.log(...Array.from({ length: envNumber + 1 }, (_, i) => i));

    //console.log(process.env.API_URL);
    //currentPage = 3;

    // const bgAfterClick = `bg-lime-500`;
    // const bgBeforeClick = `bg-indigo-600`;
    let background;

    return (
        <div className="flex flex-wrap justify-center items-center  col-span-3 h-1/3 bg-slate-500">
            <button className="bg-transparent rounded-xl p-1 m-1 border-white border-4 decoration-white text-2xl cursor-pointer hover:bg-white">
                Prev
            </button>
            {pages.map((page, index) => {
                //
                currentPage == page
                    ? (background = `bg-lime-500`)
                    : (background = `bg-indigo-600`);
                //
                if (
                    page < maxPageNumberLimit + 1 &&
                    page > minPageNumberLimit
                ) {
                    return (
                        <Button
                            key={index}
                            content={page}
                            background={background}
                            onClick={() => {
                                setCurrentPage(page);
                            }}
                        />
                    );
                } else {
                    return null;
                }
            })}
            <button
                className="text-white bg-transparent rounded-xl p-1 m-1 border-white border-4 decoration-white text-2xl  hover:bg-white"
                onClick={() => {
                    setCurrentPage(currentPage + 1);
                    if (currentPage + 1 > maxPageNumberLimit) {
                        setPageNumberLimit(
                            maxPageNumberLimit + pageNumberLimit
                        );
                    }
                }}
            >
                Next
            </button>
        </div>
    );
};
