import { createSlice } from '@reduxjs/toolkit';

const booksSlicer = createSlice({
    name: 'bookState',
    initialState: { books: [] },
    reducers: {
        setBooks: (state, action) => {
            state.books = action.payload;
        },
    },
});

const getContentSlicer = createSlice({
    name: 'getContentState',
    initialState: { getContent: [] },
    reducers: {
        setGetContent: (state, action) => {
            state.getContent = action.payload;
        },
    },
});

export const {
    actions: { setBooks },
    reducer: booksReducer,
} = booksSlicer;

export const {
    actions: { setGetContent },
    reducer: contentReducer,
} = getContentSlicer;
