                    //             //     deleteBookHandler(book.id);
                    //             // }}
                    //             content={'Delete book'}
                    //         />
                    //     </p>
                    // </div>
                ))}

                <PaginationBooks
                    totalBooks={books.length}
                    booksPerPage={booksPerPage}
                    currentPage={currentPage}
                    setCurrentPage={setCurrentPage}
                />
            </div>
        </div>
    );
};
