import React from 'react';
import { Button } from '../Button';

interface props {
    totalBooks: number;
    booksPerPage: number;
    currentPage: number;
    //setCurrentPage: () => number;
}

export const PaginationBooks = ({
    totalBooks,
    booksPerPage,
    currentPage,
}: props) => {
    //setCurrentPage,

    let pages = [];

    for (let i = 1; i <= Math.ceil(totalBooks / booksPerPage) && i < 21; i++) {
        pages.push(i);
    }

    console.log(totalBooks / booksPerPage);
    currentPage = 2;

    const bgAfterClick = `bg-lime-500`;
    const bgBeforeClick = `bg-indigo-600`;
    let bg = null;

    return (
        <div className="flex ">
            {pages.map((page, index) => {
                currentPage == page
                    ? (bg = bgAfterClick)
                    : (bg = bgBeforeClick);

                return (
                    <Button
                        key={index}
                        content={page}
                        bg={bg}
                        onClick={() => {
                            //setCurrentPage((state) => currentPage);
                            console.log(
                                'setCurrenPage((state: number) => page)'
                            );
                        }}
                    />
                );
            })}
        </div>
    );
};
