        <div className="flex ">
            {pages.map((page, index) => {
                currentPage == page ? bgAfterClick : bgBeforeClick;
                return (
                    <div key={index} className={''}>
                        <Button
                            content={page}
                            bg={bgBeforeClick}
                            onClick={() => {
                                //setCurrentPage((state) => currentPage);
                                console.log(
                                    'setCurrenPage((state: number) => page)'
                                );
                            }}
                        />
                    </div>
                );
            })}
        </div>
    );
};
